package com.example.hr.config;

import static org.junit.jupiter.api.Assertions.assertAll;
import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import com.example.hr.document.EmployeeDocument;
import com.example.hr.domain.Employee;

public class ModelMapperTest {

	@ParameterizedTest
	@CsvSource({
			"35373330300,Jack,Bauer,TR440006278659712762534346,100000,TL,IT,1965,FULL_TIME," })
	@DisplayName("Testing Employee Document to Employee Convertion")
	void employeeDocument2EmployeeConverterTest(String csvIdentity, String csvFirstName, String csvLastName, String csvIban,
			double csvMoney, String csvCurrency, String csvDepartment, int csvBirthYear, String csvJobType,
			String csvPhoto) {
		var modelMapperConfig = new ModelMapperConfig();
		var modelMapper = modelMapperConfig.modelMapper();
		var empDoc = new EmployeeDocument();
		empDoc.setIdentity(csvIdentity);
		empDoc.setFirstName(csvFirstName);
		empDoc.setLastName(csvLastName);
		empDoc.setIban(csvIban);
		empDoc.setSalary(csvMoney);
		empDoc.setCurrency(csvCurrency);
		empDoc.setBirthYear(csvBirthYear);
		empDoc.setDepartment(csvDepartment);
		empDoc.setJobType(csvJobType);
		empDoc.setPhoto(csvPhoto);
		// 2. Call Exercise Method
		var employee = modelMapper.map(empDoc, Employee.class);
		// 3. Verification
		assertAll(
			() -> assertEquals(csvIdentity, employee.getIdentity().getValue()),
			() -> assertEquals(csvFirstName, employee.getFullname().getFirstName()),
			() -> assertEquals(csvLastName, employee.getFullname().getLastName()),
			() -> assertEquals(csvIban, employee.getIban().getValue()),
			() -> assertEquals(csvMoney, employee.getMoney().getValue()),
			() -> assertEquals(csvCurrency, employee.getMoney().getCurrency().name()),
			() -> assertEquals(csvBirthYear, employee.getBirthYear().getValue())
		);
	}
}
